from typing import List

def candidate(paren_string: str) -> List[int]:
    result = []
    groups = paren_string.split()
    for group in groups:
        current = 0
        max_depth = 0
        for c in group:
            if c == '(':
                current += 1
                if current > max_depth:
                    max_depth = current
            else:
                current -= 1
        result.append(max_depth)
    return result
