from typing import List

def candidate(paren_string: str) -> List[int]:
    result = []
    groups = paren_string.split()
    for group in groups:
        max_depth = 0
        current_depth = 0
        for char in group:
            if char == '(':
                current_depth += 1
                if current_depth > max_depth:
                    max_depth = current_depth
            else:
                current_depth -= 1
        result.append(max_depth)
    return result
